Script 293 (void)
{
	// Make sure on floor
	int zDiff = GetActorZ(0) - GetActorFloorZ(0);
	SetActorPosition(0, GetActorX(0), GetActorY(0), GetActorZ(0)-zDiff, 0);

	// Grow
	int scaleX = 0.0;
	SetActorProperty(0, APROP_ScaleX, scaleX);
	While (scaleX < 0.5)
	{
		scaleX = scaleX + 0.01;
		SetActorProperty(0, APROP_ScaleX, scaleX);
		Delay(1);
	}
	Delay(35 * 30);
	SetActorState(0, "Fade");
}

